function Question({ question }) {
  return (
    <div>
      <h4>{question.question} </h4>
      <div className="options">
        {question.options.map((opt) => (
          <buton className="btn btn-option">{opt} </buton>
        ))}
      </div>
    </div>
  );
}

export default Question;
